<section id="main-content">    <section class="wrapper">        <div class="row">            <div class="col-lg-12">                <ul class="breadcrumb">                    <li><a href="<?= site_url(); ?>"><i class="fa fa-home"></i> Home</a></li>                    <li><a href="">Create Invoice</a></li>                </ul>            </div>        </div>        <div class="row">            <div class="col-lg-12">                <section class="panel">                    <header class="panel-heading">Invoice</header>                    <div class="panel-body">                        <div class="modal-content">                            <div class="modal-header">                                <h4 style="text-align: center" class="modal-title" id="mySmallModalLabel">Create Custom Invoice</h4>                            </div>                            <div class="modal-body">                                <form>                                    <br/>                                    <input type="text" class="form-control" id="amount" placeholder="Enter Amount"/>                                    <br/>                                    <input type="text" class="form-control" id="expiry" placeholder="Enter Expiry (in days)"/>                                    <br/>                                    <textarea name="description" id="desc" cols="30" rows="4" class="form-control" placeholder="Enter Description"></textarea>                                    <br/>                                    <button type="button" onclick="sendOffer()">Send Offer</button>                                </form>                            </div>                        </div>                    </div>                </section>            </div>        </div>    </section></section><script>    function sendOffer(){        var amount = $("#amount").val();        var expiry = $("#expiry").val();        var desc = $("#desc").val();        $.post('<?= site_url('home/sendOffer') ?>',{client_id:0,amount:amount,desc:desc,expiry:expiry},function(e){            console.log(e);            window.location = "<?= site_url('home/view/view_invoices'); ?>";        })    }</script>