" +            "<div class='col-sm-12'> " +            "<iframe width='560' height='315' src='" + url + "' frameborder='0' allowfullscreen></iframe>" +        "</div> " +        "<div class='row'></div> " +        "</div> " +        "</div> " +        "</div> " +        "</div> " +        "</div> ";        $("#vid").html(a);        $("#newModal").modal('toggle');    }</script>