<style>    .rating {        float:left;        width:300px;    }    .rating span { float:right; position:relative; }    .rating span input {        position:absolute;        top:0px;        left:0px;        opacity:0;    }    .rating span label {        display:inline-block;        width:30px;        height:30px;        text-align:center;        color:#FFF;        background:#ccc;        font-size:30px;        margin-right:2px;        line-height:30px;        border-radius:50%;        -webkit-border-radius:50%;    }    .rating span:hover ~ span label,    .rating span:hover label,    .rating span.checked label,    .rating span.checked ~ span label {        background:#F90;        color:#FFF;    }</style><section id="main-content">    <section class="wrapper">        <div class="row">            <div class="col-lg-12">                <ul class="breadcrumb">                    <li><a href="<?= site_url(); ?>"><i class="fa fa-home"></i> Home</a></li>                    <li><a href="">View Progress</a></li>                </ul>            </div>        </div>        <div class="row">            <div class="col-lg-12">                <section class="panel">                    <header class="panel-heading">                        Work Progress                    </header>                    <div class="panel-body">                        <a href="<?= site_url('home/view/view_invoice')."/".$progress[0]['invoice_id'] ?>"><span class="btn btn-primary">See Invoice</span></a>                        <table id="myTable" class="table table-hover">                            <thead>                            <tr>                                <td>#</td>                                <td>Invoice ID</td>                                <td>Work</td>                                <td>Description</td>                                <td>Status</td>                                <td>Date Delivered</td>                       