lec'+i)[0].checked){            var email = $("#parenmobile").val();            $("#gchildMobileval"+i).val(email);        }    }    function cchildEmail(i){        if($('#cchildEmail'+i)[0].checked){            var email = $("#parenEmail").val();            $("#cchildEmailval"+i).val(email);        }    }    function cchildMobilee(i){        if($('#cchildMobilec'+i)[0].checked){            var email = $("#parenmobile").val();            $("#cchildMobileval"+i).val(email);        }    }    function removechild(id){        $("#ChildField"+id).remove();    }    function addChild(){        var child = "<div id='ChildField"+ci+"'> " +            "<a onclick='removechild("+ ci +")'>&times;</a> " +            "<div class='row'></div> " +            "<div class='col-md-6'> " +            "<input type='text' class='form-control' name='child["+ci+"][fname]' placeholder='First Name' autofocus> " +            "</div> " +            "<div class='col-md-6'> " +            "<input type='text' class='form-control' name='child["+ci+"][lname]' placeholder='Last Name' autofocus> " +            "</div> " +            "<div class='col-md-6'> " +            "<select class='form-control' onchange='childDay("+ci+")' id='childm"+ci+"' name='child["+ci+"][month]'> ";            child += "<option value='1'>January</option> " +                "<option value='2'>February</option> " +                "<option value='3'>March</option> " +                "<option value='4'>April</option> " +                "<option value='5'>May</option> " +                "<option value='6'>June</option> " +                "<option value='7'>July</option> " +                "<option value='8'>August</option> " +                "<option value='9'>September</option> " +                "<option value='10'>October</option> " +                "<option value='11'>November</option> " +                "<option value='12'>December</option>";            child += "</select>" +   