var i = 1;i<=x;i++){                a+="<option value='"+ i +"'>"+ i +"</option>";            }        $("#childd"+ci).html(a);    }    function gchildDay(ci){        var a = "";        var month = $("#gchildm"+ci).val();        var x = getMonthDays(month);        for(var i = 1;i<=x;i++){            a+="<option value='"+ i +"'>"+ i +"</option>";        }        $("#gchildd"+ci).html(a);    }    function getMonthDays(month){        if(month == 1){            return 31;        }else if(month == 2){            return 28;        }else if(month == 3){            return 31;        }else if(month == 4){            return 30;        }else if(month == 5){            return 31;        }else if(month == 6){            return 30;        }else if(month == 7){            return 31;        }else if(month == 8){            return 31;        }else if(month == 9){            return 30;        }else if(month == 10){            return 31;        }else if(month == 11){            return 30;        }else if(month == 12){            return 31;        }    }    $("#month").change(function(){        var a = "";        var month = $("#month").val();        if(month == 1){            for(var i = 1;i<=31;i++){                a+="<option value='"+ i +"'>"+ i +"</option>";            }        }else if(month == 2){            for(var i = 1;i<=28;i++){                a+="<option value='"+ i +"'>"+ i +"</option>";            }        }else if(month == 3){            for(var i = 1;i<=31;i++){                a+="<option value='"+ i +"'>"+ i +"</option>";            }        }else if(month == 4){            for(var i = 1;i<=30;i++){                a+="<option value='"+ i +"'>"+ i +"</option>";            }        }else if(month == 5){            for(var i = 1;i<=31;i++){                a+="<option value='"+ i +"'>"+ i +"</option>";            }        }else if(month == 6){            for(var i = 1;i<=30;i++){                a+="<option value='"+ i +"'>"+ i +"</option>";            }        }else if(month == 7){            for(var i = 1;i<=31;i++){                a+="<option value='"+ i +"'>"+ i +"</option>";            }        }else if(month == 8){            for(var i = 1;i<=31;i++){                a+="<option value='"+ i +"'>"+ i +"</option>";            }        }else if(month == 9){            for(var i = 1;i<=30;i++){                a+="<option value='"+ i +"'>"+ i +"</option>";            }        }else if(month == 10){            for(var i = 1;i<=31;i++){                a+="<option value='"+ i +"'>"+ i +"</option>";            }        }else if(month == 11){            for(var i = 1;i<=30;i++){                a+="<option value='"+ i +"'>"+ i +"</option>";            }        }else if(month == 12){            for(var i = 1;i<=31;i++){                a+="<option value='"+ i +"'>"+ i +"</option>";            }        }        $("#birth_date").html(a);    });    function aageGroupp(input){//        alert(input);//        var input = $("#age_groupa").val();        if(input >= '39'){            $("#grandChild").css('display',"");        }        if(input >= '29'){            $("#child").css('display',"");        }else{            $("#child").css('display',"none");            $("#grandChild").css('display',"none");        }     };    function businessGroupa(){        var a = $("#bgroup").val();        if(a == "other"){            $("#bgroupField").css("display","");        }else{            $("#bgroupField").css("display","none");        }    }    function employment(){        var a = $("#employ").val();        if(a == "other"){            $("#employementField").css("display","");        }else{            $("#employementField").css("display","none");        }    }    function churchGroupa(){//        alert(1);        var a = $("#churchG").val();        if(a == "other"){            $("#churchGroupField").css("display","");        }else{            $("#churchGroupField").css("display","none");        }    }    function validateCode(){        var c = $("#codec").val();        $.post("<?= site_url('home/ajax/validCode'); ?>",{code:c},function(e){            $("#reg").html(e);            if(e == "Invalid"){                $("#val").css("display","");            }else{                $("#codec").attr("readonly","");                $("#val").css("display","none");            }        });    }    function termsss(){        if($('#agreeTerms')[0].checked){            checkPasswd();            $("#doreg").removeAttr("disabled");        }else{            $("#doreg").attr("disabled","disabled");        }    }</script></body></html>