<section id="main-content">    <section class="wrapper">        <div class="row">            <div class="col-lg-12">                <ul class="breadcrumb">                    <li><a href="<?= site_url(); ?>"><i class="fa fa-home"></i> Home</a></li>                    <li><a href="">Support</a></li>                </ul>            </div>        </div>        <div class="row">            <div class="col-lg-12">                <section class="panel">                    <header class="panel-heading">Help</header>                    <div class="panel-body">                        <div class="modal-content">                            <div class="modal-header">                                <h4 style="font-size:16px;font-weight: bold;" class="modal-title" id="mySmallModalLabel">                                    Get in touch with us                                </h4>                            </div>                            <div class="modal-body">                                <form method="post" action="<?= site_url('home/insert/support/index'); ?>">                                    <br/>                                    <input name="subject" type="text" class="form-control" placeholder="Subject Matter"/>                                    <br/>                                    <textarea name="msg" id="desc" cols="30" rows="4" class="form-control" placeholder="Enter Description"></textarea>                                    <br/>                                    <button class="btn btn-primary" type="submit">Send</button>                                </form>                            </div>                        </div>                    </div>                </section>            </div>        </div>    </section></section><script>    function sendOffer(){        var amount = $("#amount").val();        var expiry = $("#expiry").val();        var desc = $("#desc").val();        $.post('<?= site_url('home/sendOffer') ?>',{client_id:0,amount:amount,desc:desc,expiry:expiry},function(e){            console.log(e);            window.location = "<?= site_url('home/view/view_invoices'); ?>";        })    }</script>